//Currently unused

const gulp = require('gulp')
const babel = require('gulp-babel')
var uglify = require('gulp-uglify')
var rename = require("gulp-rename")

gulp.task('default', () => {
  return gulp.src('src/vue-apparate.js')
    .pipe(babel({
      presets: ['es2015']
    }))
    .pipe(gulp.dest('.'))
})

gulp.task('minify', () => {
  return gulp.src('vue-apparate.js')
    .pipe(uglify())
    .pipe(rename('vue-apparate.min.js'))
    .pipe(gulp.dest('.'))
})
