casper.test.begin('Apparate working properly', function suite(test) {
  casper.start('http://localhost:8080/').viewport(1600,1000)
  .wait(500, function() {
    test.assertExists('h2.apparate');
  }).then(function() {
    test.assertExists('h2.apparate.fade-in-up');
    test.assertExists('h1.fade-in-up');
  }).run(function() {
    test.done();
  });
})
